mapper.select:
SELECT COUNT(b.work_no) FROM( SELECT a.*, row_number() OVER (ORDER BY a.work_no DESC) rnum FROM( SELECT w.work_no As work_no ,w.emp_id As emp_id ,TO_CHAR(w.work_bgng_dt,'yyyy-MM-dd') AS workDt ,TO_CHAR(w.work_bgng_dt,'DY') AS workDw ,TO_CHAR(w.work_bgng_dt,'HH24:mm:ss') AS workBgngDt ,TO_CHAR(w.work_end_dt,'HH24:mm:ss') AS workEndDt , w.work_state , e.cust_id , d.dept_nm , p.jbgd_nm FROM work w, employee e, department d, position p WHERE 1=1 AND w.emp_id = e.emp_id AND e.dept_id = d.dept_id AND e.jbgd_id = p.jbgd_id AND e.cust_id = #{custId}
ORDER BY w.work_no DESC ) a ) b
WHERE b.rnum >= #{startRow} AND b.rnum <= #{endRow}

SELECT b.* FROM( SELECT a.*, row_number() OVER (ORDER BY a.work_no DESC) rnum FROM( SELECT w.work_no As work_no , w.emp_id As emp_id , e.emp_name , TO_CHAR(w.work_bgng_dt,'yyyy-MM-dd') AS workDt , TO_CHAR(w.work_bgng_dt,'DY') AS workDw , TO_CHAR(w.work_bgng_dt,'HH24:mm:ss') AS workBgngDt , TO_CHAR(w.work_end_dt,'HH24:mm:ss') AS workEndDt , w.work_state , e.cust_id , d.dept_nm , p.jbgd_nm FROM work w, employee e, department d, position p WHERE 1=1 AND w.emp_id = e.emp_id AND e.dept_id = d.dept_id AND e.jbgd_id = p.jbgd_id AND e.cust_id = #{custId}
ORDER BY w.work_no DESC ) a ) b
WHERE b.rnum >= #{startRow} AND b.rnum <= #{endRow}
