mapper.insert:

INSERT INTO work_year(
	wy_no
	, cust_id
	, wy_year
	, wy_hldy
	, wy_memo
) VALUES (
	seq_work_year.NEXTVAL
	, #{custId}
	, #{wyYear}
	, #{wyHldy}
	, #{wyMemo}
)

mapper.update:
UPDATE work_year
SET 
	wy_year = #{wyYear}
	, wy_hldy = #{wyHldy}
	, wy_memo = #{wyMemo}
WHERE wy_no = #{wyNo}

UPDATE employee e
SET e.emp_vac = #{wyHldy}
WHERE e.cust_id = #{custId}
AND TRUNC(((SYSDATE - e.emp_date) / 365) + 1) = #{wyYear}
