mapper.select:
select b.* from( select a.*, row_number() over (order by a.pstg_bgng_dt desc) rnum from( select pst_id, pst_ttl, pst_cn, emp_id, pstg_bgng_dt, ctgry_no, pst_cnt from board where 1=1 and ctgry_no=#{ctgryNo}
order by pstg_bgng_dt desc ) a ) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}

select * from attach_file
where atch_file_no=#{atchFileNo} 
	and atch_file_seq=1
