mapper.insert:
INSERT INTO approval_line(
	 aprv_line_sn
	,aprv_sn
	,emp_id
	,aprv_order
	,prgrs_stts_cd
	,aprv_se_cd
	,atrz_dt
	,atrz_opnn
	,sign_sn
) VALUES (
	 seq_approval_line.NEXTVAL
	,#{aprvSn}
	,#{empId}
	,#{aprvOrder}
	,#{prgrsSttsCd}
	,2
	,SYSDATE
	,#{atrzOpnn}
	,(
		SELECT sign_sn
		FROM approval_sign
		WHERE 1=1
		AND apply_dt = (
		    SELECT MAX(apply_dt)
		    FROM approval_sign
		    WHERE emp_id = #{empId}
		    )
		AND emp_id = #{empId}
	)
)

mapper.update:
UPDATE approval_line
SET
	 prgrs_stts_cd = #{prgrsSttsCd}
	,atrz_dt = SYSDATE
	,atrz_opnn = #{atrzOpnn}
	,sign_sn = (
				SELECT sign_sn
				FROM approval_sign
				WHERE 1=1
				AND apply_dt = (
				    SELECT MAX(apply_dt)
				    FROM approval_sign
				    WHERE emp_id = (
				    		SELECT emp_id
				    		FROM approval_line
				    		WHERE aprv_line_sn = #{aprvLineSn}
				    	)
				    )
				AND emp_id = (
				    		SELECT emp_id
				    		FROM approval_line
				    		WHERE aprv_line_sn = #{aprvLineSn}
				    	)
			)
WHERE aprv_line_sn = #{aprvLineSn}

UPDATE approval_line
SET
	prgrs_stts_cd = 7
WHERE 1=1
AND aprv_sn = #{aprvSn}
AND aprv_line_sn  < #{aprvLineSn}

UPDATE approval_line SET prgrs_stts_cd = 2 WHERE aprv_line_sn = (SELECT aprv_line_sn FROM (SELECT emp_id, aprv_order, aprv_line_sn FROM approval_line WHERE 1=1 AND aprv_sn = #{aprvSn}
AND aprv_order <
(SELECT aprv_order FROM approval_line WHERE 1=1 AND aprv_line_sn = #{aprvLineSn}) ORDER BY aprv_order DESC) WHERE ROWNUM = 1)

UPDATE approval
SET aprv_no = #{aprvNo}
WHERE aprv_sn = #{aprvSn}

